    # Determine the distribution and release
    local distro=$(lsb_release -is)
    local release=$(lsb_release -rs)
    echo $distro $release

    # Select the appropriate safe list based on the distribution and version
    local safe_packages
    if [[ "$distro" == "Ubuntu" && "$release" == "22.04" ]]; then VANILLA="$VANDPKG22"
    elif [[ "$distro" == "Ubuntu" && "$release" == "20.04" ]]; then VANILLA="$VANDPKG20"
    elif [[ "$distro" == "Debian" && "$release" == "11" ]]; then VANILLA="$VANDPKG11"
    else
        write_audit_report "Unwanted Software" "Distribution and version not recognized or not supported"
        return
    fi

    # Get a list of all installed packages
    CURDPKG=`dpkg --get-selections | grep -v lib | grep -v python | grep -v "linux-" | grep -v gir1 | grep -v "gnome-" | cut -f1`
    
    # Compare installed packages with the selected safe package list
    for I in $CURDPKG; do if [[ $VANILLA != *"$I"* ]]; then write_audit_report "Unwanted Software" "Possible unwanted package detected: $I"
        fi
    done
    
